// Parse GitHub webhook data
const data = $input.first().json;
const event = data.action || 'unknown';
const repo = data.repository?.full_name || 'Unknown repo';

let message = '';

if (data.pull_request) {
  message = `üîÄ PR ${event}: ${data.pull_request.title} in ${repo}`;
} else if (data.issue) {
  message = `üìù Issue ${event}: ${data.issue.title} in ${repo}`;
} else if (data.commits) {
  message = `üíæ New commits pushed to ${repo}`;
} else {
  message = `üì¨ ${event} event in ${repo}`;
}

return [{
  json: {
    content: message,
    url: data.pull_request?.html_url || data.issue?.html_url || data.repository?.html_url
  }
}];
